import React, { useState } from 'react'
import './Pricing.css'; // Use styles similar to Register.css
const Pricing = () => {
  const [selectedPrice, setSelectedPrice] = useState("$250k");

  // Data for pricing tiers
  const pricingData = {
    "$250k": {
      description: "For asking prices below $250k",
      fee: "8% closing fee when you sell",
      monthly: "Plus $25/mo to list",
    },
    "$250k-$1M": {
      description: "For asking prices between $250k-$1M",
      fee: "7% closing fee when you sell",
      monthly: "Plus $50/mo to list",
    },
    "$1M+": {
      description: "For asking prices above $1M",
      fee: "6% closing fee when you sell",
      monthly: "Plus $100/mo to list",
    },
  };

  // Features Data
  const features = [
    {
      icon: "ðŸ“ˆ", // Replace with actual icons/images
      title: "Maximum interest",
      description: "Attract more buyers with marketing and listing support.",
    },
    {
      icon: "ðŸ’¼",
      title: "Expert guidance",
      description: "Sell for the highest price and best terms with our help.",
    },
    {
      icon: "ðŸ”’",
      title: "Free and secure escrow",
      description: "Close safely and easily with trusted escrow partners.",
    },
  ];
  return (
   <>
 <div className="selling-price-section">
      <h2 className="section-title">
        A Custom Acquisition Plan that Scales with You
      </h2>
      <p className="section-subtitle">
        Scalable pricing based on your startupâ€™s asking price.
      </p>

      <div className="content-wrapper">
        {/* Left Column: Pricing Details */}
        <div className="pricing-column">
          <div className="price-tabs">
            {Object.keys(pricingData).map((price) => (
              <button
                key={price}
                className={`price-tab ${
                  selectedPrice === price ? "active" : ""
                }`}
                onClick={() => setSelectedPrice(price)}
              >
                {price}
              </button>
            ))}
          </div>
          <div className="pricing-details">
            <h3>{selectedPrice}</h3>
            <p>{pricingData[selectedPrice].description}</p>
            <p>{pricingData[selectedPrice].fee}</p>
            <p>{pricingData[selectedPrice].monthly}</p>
            <a href="#" className="cta-button">
              Get Started
            </a>
          </div>
        </div>

        {/* Right Column: Features */}
        <div className="features-column">
          {features.map((feature, index) => (
            <div className="feature" key={index}>
              <div className="feature-icon">{feature.icon}</div>
              <div>
                <h4>{feature.title}</h4>
                <p>{feature.description}</p>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
</>

  )
}

export default Pricing
